import React, { useState } from 'react';
import axios from 'axios';

function App() {
    const [input, setInput] = useState('');
    const [mindMap, setMindMap] = useState(null);

    const handleInputChange = (event) => {
        setInput(event.target.value);
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await axios.post('http://localhost:5000/mindmap', { input });
            setMindMap(response.data);
        } catch (error) {
            console.error('Error fetching mind map:', error);
        }
    };

    return (
        <div>
            <h1>ChatMindMap Application</h1>
            <form onSubmit={handleSubmit}>
                <input type="text" value={input} onChange={handleInputChange} />
                <button type="submit">Generate Mind Map</button>
            </form>
            {mindMap && (
                <div>
                    <h2>Mind Map:</h2>
                    <p>Central Node: {mindMap.central_node}</p>
                    <ul>
                        {mindMap.sub_nodes.map((node, index) => (
                            <li key={index}>{node}</li>
                        ))}
                    </ul>
                </div>
            )}
        </div>
    );
}

export default App;
