import React, { useEffect, useRef } from 'react';
import { DataSet, Network } from 'vis-network/standalone/esm/vis-network';

const MindMap = ({ data }) => {
    const domNode = useRef(null);

    useEffect(() => {
        if (!data) return;

        // Nodes and edges data
        const nodes = new DataSet([
            { id: 1, label: data.main_topic, color: '#f0a30a', shape: 'ellipse' },
            ...data.subtopics.map((subtopic, index) => ({
                id: index + 2,
                label: subtopic,
                color: '#7cbb00',
                shape: 'box'
            }))
        ]);

        const edges = data.subtopics.map((subtopic, index) => ({
            from: 1,
            to: index + 2,
            arrows: 'to'
        }));

        // Configuration for the Network
        const options = {
            nodes: {
                borderWidth: 2,
                size: 30,
                color: {
                    border: '#406897',
                    background: '#6AAFFF'
                },
                font: { color: '#eeeeee' }
            },
            edges: {
                color: 'lightgray',
                smooth: true
            },
            layout: {
                hierarchical: {
                    direction: "UD",
                    sortMethod: "directed"
                }
            },
            interaction: { hover: true }
        };

        // Initialize network
        const network = new Network(domNode.current, { nodes, edges }, options);

        return () => {
            network.destroy();
        };
    }, [data]); // Re-run effect if data changes

    return <div ref={domNode} style={{ height: '500px', width: '100%' }} />;
};

export default MindMap;
